import React, { useContext } from 'react';
import { DataConsumer } from './dataContext';
class Auth extends React.Component {
  state = {
    email: '',
    password: '',
  };
  handleInput = ({ target: { name, value } }) => {
    this.setState({ [name]: value });
  };
  componentDidMount() {
    const dataInfo2 = this.context;
    console.log(dataInfo2, 'datainfo2');
  }

  render() {
    return (
      <DataConsumer>
        {(props) => {
          return (
            <div className='auth'>
              <form
                onSubmit={(e) => {
                  let { email, password } = this.state;
                  e.preventDefault();
                  props.loginHandler(email, password);
                  props.handleModal(false);
                }}
              >
                <h3 className='center'>Admin Login</h3>
                <label htmlFor='email'>Email:</label>
                <input
                  type='email'
                  id='email'
                  name='email'
                  placeholder='Enter your email'
                  value={this.state.email}
                  onChange={this.handleInput}
                />
                <label htmlFor='password'>Password:</label>
                <input
                  type='password'
                  id='password'
                  name='password'
                  placeholder='Enter the password'
                  value={this.state.password}
                  onChange={this.handleInput}
                />
                <button className='auth-btn'>Login</button>
                <div
                  className='auth-cancel'
                  onClick={() => props.handleModal(false)}
                >
                  <i className='fal fa-times'></i>
                </div>
                <p className='password-hint'>
                  Please use admin@admin.com as email and qwerty as password to
                  login.
                </p>
              </form>
            </div>
          );
        }}
      </DataConsumer>
    );
  }
}

export default Auth;
